```c
#include <stdio.h>
#include <stdlib.h>

typedef int Elemtype;

//节点结构
typedef struct DulNode{
    struct DulNode * prior;
    int data;
    struct DulNode * next;
}DulNode,*DulLinkList;

DulNode* initDulNode(DulNode * L,int length){
    //创建一个首元节点，链表的头指针为L
    L=(DulNode*)malloc(sizeof(DulNode));
    //对节点进行初始化
    L->prior=NULL;
    L->next=NULL;
    L->data=NULL;
    //声明一个指向首元节点的指针，方便后期向链表中添加新创建的节点
    DulNode *r=L;
    for (int count=0; count<length; count++) {
        //创建新的节点并初始化
        DulNode *p=(DulNode*)malloc(sizeof(DulNode));
        p->prior=NULL;
        p->next=NULL;
        printf("Please enter data %d:",count);
        scanf("%d",&p->data);
        //新节点与链表最后一个节点建立关系
        r->next=p;
        p->prior=r;
        //r永远指向链表中最后一个节点，重点！！！！
        r=r->next;
    }
    //返回新创建的链表
    return L;
}
void display(DulNode * L){
    DulNode *temp=L->next;
    while (temp) {
        //如果该节点无后继节点，说明此节点是链表的最后一个节点
        if (temp->next==NULL) {
            printf("%d\n",temp->data);
        }else{
            printf("%d <-> ",temp->data);
        }
        temp=temp->next;
    }
}


int main() {
    //创建一个头指针
    DulNode * L=NULL;
    //调用链表创建函数
    L=initDulNode(L,5);
    //输出创建好的链表
    display(L);
    //显示双链表的优点
    return 0;
}


