        console.log("The account ing meets each monday.");
    }

    generateReports():void{

    }
}



```

##### 把类当接口使用

```ts
class Poing{
    x:number;
    y:number;
}

interface Point3D extends Point{
    z:number;
}

let point3d:Point3D={
    x:1,
