                        var item = this.list[i];
                        total += item.price * item.count;
                    }
                    return total.toString().replace(/\B(?=(\d{3})+$)/g, ',');
                }
            },
        })
    </script>
    <script src="../Nowebpack/js/shopping_car.js"></script>
</body>

</html>
```

